package co.rsk.bridge.btcLockSender;

import co.rsk.bridge.btcLockSender.BtcLockSender.TxSenderAddressType;
import java.util.HashMap;
import java.util.Map;

/*
 * Raw transactions sent from different addresses type
 * Used for testin BtLockSender implementations
 */
public final class RawTransactions {

    // Prevents instantiation
    private RawTransactions() {
    }

    public final static Map<TxSenderAddressType, String> txs;

    static {
        txs = new HashMap<>();
        txs.put(TxSenderAddressType.P2PKH, "");
        txs.put(TxSenderAddressType.P2SHP2WPKH, "");
        txs.put(TxSenderAddressType.P2SHMULTISIG, "");
        txs.put(TxSenderAddressType.P2SHP2WSH, "");
    }
}
