    @ParameterizedTest
    @MethodSource("txAndRedeemScriptArgProvider")
    void test_extractRedeemScriptFromInput_pegouts_and_migrations(String btcTxHex, String redeemScriptHex) {
        // Arrange
        BtcTransaction btcTx = new BtcTransaction(btcMainnetParams, Hex.decode(btcTxHex));
        Script expectedRedeemScript = new Script(Hex.decode(redeemScriptHex));

        Assertions.assertFalse(btcTx.getInputs().isEmpty());
        // Act
        Optional<Script> redeemScript = BitcoinUtils.extractRedeemScriptFromInput(btcTx.getInputs().get(FIRST_INPUT_INDEX));

        // Assert
        Assertions.assertTrue(redeemScript.isPresent());
        Assertions.assertEquals(expectedRedeemScript, redeemScript.get());
    }

