    @Test
    void test_extractRedeemScriptFromInput_p2sh_pegin_v1() {
        // Arrange
        // https://mempool.space/testnet/tx/2d3d0ac33fbcacab56cab594a68f9bd95c4786204e575bfb2297154d68cc115b
        BtcTransaction btcTx = new BtcTransaction(btcMainnetParams, Hex.decode(""));
        Script scriptSig = new Script(Hex.decode(""));

        Assertions.assertFalse(btcTx.getInputs().isEmpty());

        List<ScriptChunk> scriptSigChunks = scriptSig.getChunks();
        Script expectedRedeemScript = new Script( scriptSigChunks.get(scriptSigChunks.size()- 1).data);

        // Act
        Optional<Script> redeemScript = BitcoinUtils.extractRedeemScriptFromInput(btcTx.getInputs().get(FIRST_INPUT_INDEX));

        // Assert
        Assertions.assertTrue(redeemScript.isPresent());
        Assertions.assertEquals(expectedRedeemScript, redeemScript.get());
    }

    @Test
    void test_extractRedeemScriptFromInput_migration_multiple_inputs() {
        // Arrange
        BtcTransaction btcTx = new BtcTransaction(btcMainnetParams, Hex.decode(""));
        Script expectedRedeemScript = new Script(Hex.decode(""));
        List<TransactionInput> inputs = btcTx.getInputs();
        Assertions.assertFalse(inputs.isEmpty());

        // Act
        for (int i = 0; i < inputs.size(); i++) {
            TransactionInput transactionInput = inputs.get(i);
            Optional<Script> redeemScript = BitcoinUtils.extractRedeemScriptFromInput(transactionInput);

            // Assert
            Assertions.assertTrue(redeemScript.isPresent());
            Assertions.assertEquals(expectedRedeemScript, redeemScript.get());
        }
    }
